export function createListItem(){
    const list = document.querySelector('.list');

    for (let i = 1; i <= 100; i++) {
        const li = document.createElement('li');
        li.classList.add('list-item');
        li.textContent = `${i}`;

        list.appendChild(li);

        li.addEventListener('mouseenter', (event) => {
            const hoveredItem = event.target;
            const prevSibling = hoveredItem.previousElementSibling;
            const nextSibling = hoveredItem.nextElementSibling;

            hoveredItem.style.transform = 'translateX(40px)';
            hoveredItem.style.zIndex = '2';
            hoveredItem.style.transition = 'transform 0.2s ease-in-out';
            hoveredItem.style.boxShadow = '1px 2px 2px 0px #ccc';
            hoveredItem.style.cursor = 'pointer';

            if (prevSibling) {
            prevSibling.style.transform = 'translateX(20px)';
            prevSibling.style.transition = 'transform 0.2s ease-in-out';
            }
            if (nextSibling) {
            nextSibling.style.transform = 'translateX(20px)';
            nextSibling.style.transition = 'transform 0.2s ease-in-out';
            }
        });

        li.addEventListener('mouseleave', (event) => {
            const hoveredItem = event.target;
            const prevSibling = hoveredItem.previousElementSibling;
            const nextSibling = hoveredItem.nextElementSibling;

            hoveredItem.style.transform = 'none';
            hoveredItem.style.zIndex = '1';
            hoveredItem.style.transition = 'transform 0.2s ease-in-out';
            hoveredItem.style.boxShadow='none'

            if (prevSibling) {
            prevSibling.style.transform = 'none';
            prevSibling.style.transition = 'transform 0.2s ease-in-out';
            }
            if (nextSibling) {
            nextSibling.style.transform = 'none';
            nextSibling.style.transition = 'transform 0.2s ease-in-out';
            }
        }); 

        li.addEventListener('click', (event) => {
            const body = document.body;
            const clickedItem = event.target;
            const listItemRect = clickedItem.getBoundingClientRect();
            const listItemTop = listItemRect.top;
            const listItemLeft = listItemRect.left;
            const listItemWidth = listItemRect.width;
            const listItemHeight = listItemRect.height;

            // Create a card view element
            const card = document.createElement('div');
            card.classList.add('card');
            card.textContent = clickedItem.textContent;
            body.appendChild(card);

            // Center the card view
            // const screenWidth = window.innerWidth;
            // const screenHeight = window.innerHeight;
            // const cardTop = (screenHeight - listItemHeight) / 2;
            // const cardLeft = (screenWidth - listItemWidth) / 2;
            // card.style.top = `${cardTop}px`;
            // card.style.left = `${cardLeft}px`;

            // Resize the clicked item to pop out
            clickedItem.style.width = '500px';
            clickedItem.style.height = '500px';
            clickedItem.style.backgroundColor = 'white';
            clickedItem.style.zIndex = '10';
            clickedItem.style.transform = 'translate(-50%,-50%)';
            clickedItem.style.position= 'absolute';
            clickedItem.style.top = '50%';
            clickedItem.style.left = '50%'
            clickedItem.style.transition = 'transform 0.2s ease-in-out';

            // Add a dimmed background
            const dimmedBg = document.createElement('div');
            dimmedBg.classList.add('dimmed-bg');
            body.appendChild(dimmedBg);

            // Restore to the list view when dimmed background is clicked
            dimmedBg.addEventListener('click', () => {
                clickedItem.style.transform = 'none';
                clickedItem.style.zIndex = '1';
                clickedItem.style.transition = 'transform 0.2s ease-in-out';
                body.removeChild(card);
                body.removeChild(dimmedBg);
            });
        });
    }
}

createListItem();
